func Answer1(i string) int {
	floor := 0
	instructions := strings.Split(i, "")
	for _, x := range instructions {
		switch x {
		case "(":
			floor++
		case ")":
			floor--
		}
	}
	return floor
}

func Answer2(i string) int {
	floor := 0
	instructions := strings.Split(i, "")
	for y, x := range instructions {
		switch x {
		case "(":
			floor++
		case ")":
			floor--
		}
		if floor == -1 {
			return y + 1
		}
	}
	return 0
}
